mapper.select:
select di.id, di.name, di.path, di.method, di.content_type AS contentType, di.remarks, di.create_time AS createTime, di.update_time AS updateTime, di.creator,du.name AS creatorName, di.modifier,duer.name AS modifierName, di.project, dp.name AS projectName, dpm.power from d_interface di LEFT JOIN d_user du ON di.creator = du.id LEFT JOIN d_user duer ON di.modifier = duer.id LEFT JOIN d_project dp ON di.project = dp.id LEFT JOIN d_power_mapping dpm ON dpm.user_id = #{userId} AND dpm.project_id = #{project}
di.project = #{project}
and di.id = #{id}
and di.name like #{name}
order by di.update_time desc limit #{offset},#{size}

select count(*)
from d_interface


    id = #{id}


    and
    name like #{name}


    and
    project = #{project}
