mapper.select:
select id, name, path, method, content_type, creator, modifier, request, response,
project, remark, create_time, update_time
from d_interface


    id = #{id}


    and
    name like #{"%"+name+"%"}


    and
    project = #{project}


order by update_time desc
limit #{offset},#{size}

select count(*)
from d_interface


    id = #{id}


    and
    name like #{"%"+name+"%"}


    and
    project = #{project}
