mapper.select:
select id, name, creator, modifier, remarks, model, ui, web, create_time, update_time
from d_project
where id = #{id,jdbcType=INTEGER}

select id, name, creator, modifier, remarks, model, ui, web, create_time, update_time
from d_project
where id = #{id,jdbcType=INTEGER}

select id, name, creator, modifier, remarks, model, ui, web, create_time, update_time
from d_project


    id = #{id}


    and
    name like #{"%"+name+"%"}


    and
    creator = #{creator}


    and
    modifier = #{modifier}


order by update_time desc
